import React, { useState } from "react";
import "./background.css";

export function BackgroundBlock() {
  const [foundIcons, setFoundIcons] = useState([]);

  const onClick = (iconName) => {
    if (!foundIcons.includes(iconName)) {
      setFoundIcons([...foundIcons, iconName]);
    }
  };

  const isIconFound = (iconName) => {
    return foundIcons.includes(iconName);
  };

  return (
    <div className="main-container__background-image">
      <button
        className={`background-image__button--calendar ${
          isIconFound("calendar") ? "found" : ""
        }`}
        onClick={() => onClick("calendar")}
      >
        <img src="../image/calendar.png" alt="calendar" />
      </button>
      <button
        className={`background-image__button--boombox ${
          isIconFound("boombox") ? "found" : ""
        }`}
        onClick={() => onClick("boombox")}
      >
        <img src="../image/boombox.png" alt="boombox" />
      </button>
      <button
        className={`background-image__button--bag ${
          isIconFound("bag") ? "found" : ""
        }`}
        onClick={() => onClick("bag")}
      >
        <img src="../image/bag.png" alt="bag" />
      </button>
      <img src="../image/background-img.jpg" alt="space image" />
    </div>
  );
}
