import"./background.css"
import React, {useState} from "react"

export function BackgroundBlock({icons}){
    const [foundIcons, setFoundIcons] = useState([]);

    const onClick = (iconName) =>{
        if(!foundIcons.includes(iconName)){
            setFoundIcons([...foundIcons, iconName]);
        }

        const isIconFound = (iconName) =>{
            return foundIcons.includes(iconName);
        }
    }
    return(
    <div className="main-container__background-image">
        {icons.map((elem) => {
            return <button className = {`"background-image__button" ${isIconFound (elem.name)? "found": ""}`} 
            style={{top: `${elem.top}%`, left: `${elem.left}%`}}  onClick={() => onClick (elem.name)}>
                <img src={elem.picture} alt={elem.alt}/>   
            </button>
        })}
        <img src="../image/background-img.jpg" alt="space image"></img>
    </div>
    );
}
